public class NetworkAddress {
    private HttpUrl url;
    private final Dns resolver;
    private final SocketFactory socketFactory;
    private final SSLSocketFactory secureSocketFactory;
    private final HostnameVerifier verifier;
    private final CertificatePinner pinner;
    private final Authenticator auth;
    private final Proxy netProxy;
    private final List<Protocol> supportedProtocols;
    private final List<ConnectionSpec> connectionSpecifications;
    private final ProxySelector proxySelector;

    public NetworkAddress(String host, int port, Dns resolver, SocketFactory socketFactory,
                          SSLSocketFactory secureSocketFactory, HostnameVerifier verifier,
                          CertificatePinner pinner, Authenticator auth, Proxy netProxy,
                          List<Protocol> protocols, List<ConnectionSpec> connectionSpecs, 
                          ProxySelector selector) {

        // Initiating URL, fixed scheme check using correct variable
        this.url = new HttpUrl.Builder()
            .scheme(determineScheme(secureSocketFactory)) // Fixed line: uses secureSocketFactory for scheme check
            .host(host)
            .port(port)
            .build();

        // Validation checks with nested control flow
        if (resolver == null) throw new IllegalArgumentException("resolver == null");
        this.resolver = resolver;

        if (socketFactory == null) throw new IllegalArgumentException("socketFactory == null");
        this.socketFactory = socketFactory;

        if (auth == null) throw new IllegalArgumentException("auth == null");
        this.auth = auth;

        if (protocols == null || protocols.isEmpty()) {
            throw new IllegalArgumentException("protocols == null or empty");
        }
        this.supportedProtocols = Util.immutableList(protocols);

        if (connectionSpecs == null || connectionSpecs.isEmpty()) {
            throw new IllegalArgumentException("connectionSpecs == null or empty");
        }
        this.connectionSpecifications = Util.immutableList(connectionSpecs);

        if (selector == null) throw new IllegalArgumentException("proxySelector == null");
        this.proxySelector = selector;

        this.netProxy = netProxy;
        this.secureSocketFactory = secureSocketFactory;
        this.verifier = verifier;
        this.pinner = pinner;
    }

    private String determineScheme(SSLSocketFactory secureSocketFactory) {
        if (secureSocketFactory != null) {
            return "https";
        } else {
            return "http";
        }
    }
}

